class Solution {
    public int splitArray(int[] arr, int m) {
        if (arr.length == 0)
            return 0;
        int sum = 0, maxEl = -1;
        for (int i: arr) {
            sum += i;
            maxEl = Math.max(maxEl, i);
        }
        int largestSum = bs(arr, maxEl, sum, m);
        return largestSum;
    }
    private int bs(int[] arr, int low, int high, int m) {
        if (low > high)
            return low;
        int mid = (low + high) / 2;
        if (countSplits(arr, mid) <= m)
            return bs(arr, low, mid - 1, m);
        else
            return bs(arr, mid + 1, high, m);
    }
    private int countSplits(int[] arr, int mid) {
        int splits = 1;
        int curSum = 0;
        for (int i: arr) {
            curSum += i;
            if (curSum > mid) {
                splits++;
                curSum = i;
            }
        }

        return splits;
    }

}
