      if (i == n || m == 0)
        return;

      if(cache.ContainsKey(key)) return;

      curSum += nums[i];
      DFS_Memoiz_Dic(i + 1, m, curSum, maxSumSofar);
      DFS_Memoiz_Dic(i + 1, m - 1, 0, Math.Max(maxSumSofar, curSum));
    }

