    preSum[0] = 0;
    for (var i = 1; i <= n; i++)
      preSum[i] += nums[i-1] + preSum[i-1];

    var memo = new int[n, M + 1];
    //return DFS_Memoize_Array(0, M); //29 / 30 test cases passed. TLE

    for(var i = 0; i < n; ++i)
      for(var j = 0; j < M + 1; ++j)
        memo[i, j] = -1;

    return DFS_Memoize_Array(0, M); //29 / 30 test cases passed. TLE

    int DFS_Memoize_Array(int start, int m){
      if(m == 1){
        return memo[start, m] = preSum[n] - preSum[start];
      }

      if (memo[start, m] != -1) return memo[start, m];

      var max = int.MaxValue;
      for(var i = start; i < n - 1; ++i){
        var curSum = preSum[i + 1] - preSum[start];
        var maxRightPartition = DFS_Memoize_Array(i + 1, m - 1);
        max = Math.Min(max, Math.Max(curSum, maxRightPartition));
      }
      return memo[start, m] = max;
    }        
  }
}
