class Solution {
    public int splitArray(int[] nums, int m) {
        // algorithm: binary search + greedy 
        // the answer must be in range of [maximum nums[i], sum(nums)]
        // for each possible value mid, compute the minumum number of subarrays we can split nums into.
        // the minimum number of subarrays can be obtained by greedy algorithm: we add next elments into current subarray
        // until the sum of it is greater than mid.
        // if the minimum number of subarrays is larger than m, then mid is absolutely too small. so we check the right part
        // if the minimum number of subarrats is greater than or equal to m. then we need to check left part to make sure
        // it converges to a value that is a sum of some subarray in the split.
        int n = nums.length;
        int max = nums[0];
        int sum = 0;
        for (int i = 0; i < n; i++) {
            sum += nums[i];
            max = Math.max(max, nums[i]);
        }
        int left = max;
        int right = sum;
        while (left <= right) {
            int mid = left + (right - left) / 2;
            int count = split(nums, mid);
            if (count > m) {
                left = mid + 1;
            } else {
                right = mid - 1;
            }
        }
        return left; // this return value must be a sum of a subarray bc this is the smallest value that satisfies the requirement that the whole array is split into m parts
    }

    private int split(int[] nums, int limit) {
        int n = nums.length;
        int count = 0;
        int subSum = 0;
        for (int i = 0; i < n; i++) {
            subSum += nums[i];
            if (subSum > limit) {
                count++;
                subSum = nums[i];
            }
        }
        count++;
        return count;
    }
}

