class Solution {
    public int splitArray(int[] nums, int m) {
        int n = nums.length;
        int[] S = new int[n+1]; // for spliting array
        S[0] = 0; 
        for (int i =0;i < n;i++)
        {
            S[i+1] = S[i] + nums[i]; // adding number in subarray S
        }

        int[][] dp = new int[m+1][n+1];
        for (int i =0; i<=m;i++)
        {
            for (int j =0;j<=n;j++)
                dp[i][j] = Integer.MAX_VALUE;  // intialize with maxvalue
        }

      dp[0][0] = 0;  
        for (int i =1;i <= m ;i++)
        {
            for (int j=1; j<= n;j++)
            {
               for (int k = 0; k < j; ++k)
                dp[i][j] = Math.min(dp[i][j], Math.max(S[j] - S[k], dp[i - 1][k]));
            }
        }
        return dp[m][n];
    }

}

