            sum+=i;
        long long start = *max_element(nums.begin(),nums.end());
        long long end = sum;
        long long res = -1;
        while(start<=end){
            long long mid = start+(end-start)/2;

            if(isvalid(nums,mid,m)==true){
                res=mid;
                end = mid-1;
