var splitArray = function(nums, m) {
    let min = 0; // the smallest element in the array
    let max = 0; // the total cumulative sum of array
    for (const num of nums) {
        min = Math.min(min, num);
        max += num;
    }
    let ans = max;
    while (min <= max) {
        let mid = Math.floor(min + (max - min) / 2);
        let tempRes = divideGroups(nums, mid);
        if (tempRes[0] <= m) {
            if (tempRes[0] === m) ans = Math.min(ans, tempRes[1])
            max = mid - 1;
        } else {
            min = mid + 1;
        }
    }
    return ans;
};

// divide into groups so that each contingous subarray sum does not exceed mid
// return [count, maxSum]
function divideGroups(nums, mid) {
    let cnt = 1;
    let maxSum = 0;
    let sum = 0;
    for (const num of nums) {
        if (sum + num > mid) {
            cnt++;
            maxSum = Math.max(maxSum, sum);
            sum = num;
        } else {
            sum += num;
        }
    }
    return [cnt, Math.max(maxSum, sum)];
}
