class Solution {
public:
    int splitArray(vector<int>& nums, int k) {
        // ideas dp[x][k] -> What is the answer considering elements from [0, x], using exactly k intervals
        // dp[j][k] -> min(  max(dp[i][k - 1], sum(i + 1, k)))..
        int len = static_cast<int>(nums.size());
        vector< int > prefix_sum(len, 0); prefix_sum[0] = nums[0];
        for(int i = 1; i < len; ++i) prefix_sum[i] = nums[i] + prefix_sum[i - 1];

        vector< vector<int> > dp(len, vector<int>(k + 1, -1));

        auto sum = [&] (int l, int r) {
            return prefix_sum[r] - (l > 0 ? prefix_sum[l - 1] : 0);
        };

        // let's set the base-case
        for(int i = 0; i < len; ++i) dp[i][1] = sum(0, i);

        for(int tk = 2; tk <= k; ++tk) {
            for(int pos = tk - 1; pos < len; ++pos) {
                for(int previous_endpoint = tk - 2; previous_endpoint < pos; ++previous_endpoint) {
                    if(dp[previous_endpoint][tk - 1] != -1) {
                        if(dp[pos][tk] == -1) dp[pos][tk] = max(dp[previous_endpoint][tk - 1], sum(previous_endpoint + 1, pos));
                        else dp[pos][tk] = min(dp[pos][tk], max(dp[previous_endpoint][tk - 1], sum(previous_endpoint + 1, pos)));
                    }
                }
            }
        }

        return dp[len - 1][k];
    }
};

