def numberOfArithmeticSlices(self, A: List[int]) -> int:
	diffs = [b-a for a, b in zip(A, A[1:])]
	l = r = cnt = 0  # left, right pointers and counter
	while r < len(diffs):
		if diffs[r] == diffs[l]: 
			cnt += r - l
		else: 
			l = r
		r += 1
	return cnt
