class Solution {
public:
    int numberOfArithmeticSlices(vector<int>& a) {
        if(a.size()<3)
            return 0;

        int ans =0, s = 0, len, d = a[1] - a[0];
        for(int i=1;i<a.size(); i++)
        {
            if(a[i] != a[i-1] + d)
            {
                len = i-s;
                if(len > 2)
                    ans += ( (len-2)*(len-1) )/2;
                d = a[i] - a[i-1];
                s = i-1;
            }
        }

        len = a.size() - s;
        if(len > 2)
            ans += ( (len-2)*(len-1) )/2;

        return ans;
    }
};
