class Solution {
public:
    int calcTotal(int k) {
        return ((k-1)*(k-2))/2;
    }

    int numberOfArithmeticSlices(vector<int>& nums) {
        int n = (int)nums.size();
        if(n<3) return 0;

        int cnt = 0, diff = INT_MIN;
        int ans = 0;
        for(int i=1; i<n; ++i) {
            if(nums[i]-nums[i-1] == diff) {
                ++cnt;
            } else {
                if(cnt>2) ans += calcTotal(cnt);
                cnt = 2;    // any two numbers in sequence would always be in arithmetic
                diff = nums[i]-nums[i-1];
            }
        }

        ans += calcTotal(cnt);
        return ans;
    }
};
```

