class Solution {
public:
    int numberOfArithmeticSlices(vector<int>& A) {
        int res  = 0;
        deque<int> vec;
        for (int i = 1; i < A.size(); i++) {
            vec.push_back(A[i] - A[i - 1]);
            while (vec.front() != vec.back())
                vec.pop_front();
            res += max(0, (int)vec.size() - 1);
        }
        return res;
    }
};
