    }

    int numberOfArithmeticSlices(vector<int>& A) {
        int m = A.size();
        vector<vector<int> > dp(m,vector<int>(m,0));
        int count=0;

        //Check for Length=3
        for(int i=0;i<=(m-3);i++) {

                dp[i][i+2]=consecutive(A,i,i+1,i+1,i+2) ? 1 : 0;

                if(dp[i][i+2]) count++;

        }

       //Check for Length 4 to Length m
        for(int l=4;l<=m;l++) {
            for(int i=0;i<=(m-l);i++) {
                int j = i + l -1;

                dp[i][j] = dp[i][j-1] && consecutive(A,i,i+1,j-1,j) ? 1 : 0;
                if(dp[i][j]) count++;

            }
        }

        return count;
    }
};
