var numberOfArithmeticSlices = function(nums) {
    const length = nums.length;
    let result = 0;

    counting: for (let start = 0; start < length - 2; start++) {
        let numberOfConsElements = 1;
        let difference = null;

        for (let j = start + 1; j < length; j++) {
            const currentDifference = nums[j] - nums[j - 1];

            if (difference === null) {
                difference = currentDifference;
            } else if (difference !== currentDifference) {
                continue counting;
            }

            if (++numberOfConsElements >= 3) {
                result++;
            }
        }
    }  

    return result;
};
