        l,r,diff,ans,cur=0,2,nums[1]-nums[0],0,0
        while(r<len(nums)):
            if((nums[r]-nums[r-1])==diff):
                cur+=1
            else:
                ans+=(cur*(cur+1))//2
                cur=0
                l=r-1
                diff=nums[r]-nums[l]
            r+=1
        if(not cur==0):
            ans+=(cur*(cur+1))//2
        return ans

