        count = 1; /* By default, we are assuming that with given diff, we got atleast 1 pair */

        /* 
         * Loop till next numbers which are having same diff 
         * For e.g. 1, 2, 3, 4, 5 -> This will iterate till 4th index and gather the 
         *  subarray length in `windows` array. 
        */
        while(nums[i + 1] - nums[i] === diff) {
            i++;
            count++;
        }               

        /* For 3 numbers with same diff, count will be 2. Count will always be less than sub array length of same diff numbers */
        if (count >= 2) {
            /* Push the correct sub array length increamenting it by 1. */
            /* For e.g. 1, 4,5,6, 8 array, subarray will be 4,5,6. For which count will be 2 which is 1 less than the required subarray length*/
            windows.push(count + 1);
        }
    }

    return windows.reduce((total, windowSize) => total + getPossibility(windowSize, 3), 0);
};


const getPossibility = (fullLength, segmentLength = 3) => {
    const n = fullLength - segmentLength + 1;
    return n * (n + 1) / 2;
}

