class Solution {
    public int numberOfArithmeticSlices(int[] nums) {
        if(nums.length < 3)
            return 0; //base case

        int result = 0;

        for(int i = 0; i < nums.length - 2; i++){

            int difference = nums[i+1]-nums[i];
            int start = i + 1;
            int end = i + 2;

            while(end < nums.length){
            int currDifference = nums[end]-nums[start];
            if(currDifference == difference){
                result++;   
                end++;
                start++;
            }
            else
                break;
            }
        }

        return result;

    }
}
