class Solution {
    public int numberOfArithmeticSlices(int[] a) {

	// need minimum 3 elements in subarray
        if(a.length <= 2) return 0;

        int count =0, start = 1;

        for(int i=2;i<a.length;i++){

		// If the arithmetic condition is true, we can use our formula to add the current element to the list
            if(a[i] - a[i-1] == a[i-1] - a[i-2] )  count += i - start;

		// if we acquire a new difference, the prev arithmetic series will end and  we can start checking for the new difference by making new arithmetic series			
		//we mark the start of the new arithmetic series
            else start = i; 
        }     

		// return the total subarrays possible i.e the count
        return count;   
    }
}

