class Solution {
private:
    void backtrack(int &ans,vector<int>&curr,int index,vector<int>&nums){
        if(index>=nums.size()){
            return;
        } else if(index==0){
            for(int i=index;i<nums.size();i++){
                curr.push_back(nums[i]);
                backtrack(ans,curr,i+1,nums);
                curr.pop_back();
            }
        } else if(curr.size()<2){
            curr.push_back(nums[index]);
            backtrack(ans,curr,index+1,nums);
            curr.pop_back();
        } else {
            int n=curr.size();
            if(curr[n-1]-curr[n-2]==nums[index]-curr[n-1]){
                ans++;
                curr.push_back(nums[index]);
                backtrack(ans,curr,index+1,nums);
                curr.pop_back();
            }
        }
    }
public:
