This is a variation of matrix chain multiplication.
```
class Solution {
public:
    int numberOfArithmeticSlices(vector<int>& nums) {
        int n = nums.size();
        if(n < 3) return 0;
        vector<vector<int>> dp(n, vector<int>(n, 0));
        for(int i = 0; i < n; i++) dp[i][i] = nums[i];
        for(int i = 0; i < n - 1; i++){
            int j = i + 1;
            dp[i][j] = dp[i + 1][j] - dp[i][j - 1];
        }
        int ans = 0;
        for(int i = 0; i < n - 2; i++){
            int j = i + 2;
            if(dp[i + 1][j] == dp[i][j - 1]) dp[i][j] = 1;
            else{
                dp[i][j] = 0;
            }
        }
        for(int len = 4; len <= n; len++){
            for(int i = 0; i < n - len + 1; i++){
                int j = i + len - 1;
                if(dp[i + 1][j] == dp[i][j - 1] && dp[i + 1][j] != 0) dp[i][j] = dp[i + 1][j] + 1;
                else{
                    dp[i][j] = dp[i + 1][j];
                }
            }
        }
        for(int i = 2; i < n; i++){
            ans += dp[0][i];
        }
        return ans;
    }
};
```

