class Solution {
public:
    int numberOfArithmeticSlices(vector<int>& A) {
        //return 0 if A is empty

        if(A.size()==0){
            return 0;
        }


        //take a variable count to store the answer
        int count = 0 ;

        //counter will keep the count of consecutive occuring same difference
        //lastNum will keep track of last difference
        int lastNum = INT_MAX , counter = 1;

        for(int i=0 ; i<A.size()-1  ;  i++ ){

            //val is difference of two consecutive numbers 
            int val =  A[i]  -  A[i+1]  ;
            if(val == lastNum){
                //if val is same as last num , increase the counter
                counter++;
            }else{
                //if val is not same as last num then , 
                // use it like this
                //for ex we have counter = 3 , then we have 3 times occurance of same difference
                //let it is 2 , 2 , 2
                // so we can have these options [2,2] , [2,2] , [2,2,2]
                //this means sum of natural numbers till counter-1
                //hence formula is n*(n+1)/2 , put counter-1 in formula
                //we get ans = (counter-1)*(counter)/2

                count  +=   ( (counter)*(counter-1) )/2;
                lastNum = val;
                counter = 1;
            }

        }

        count  +=   ((counter)*(counter-1))/2;

        //return ans
        return  count;
    }
};
```

