class Solution {
public:
    int trap(vector<int>& height) {
        // Base condition...
        if (height.empty())
            return 0;
        // To store the maximum water that can be stored..
        int trap = 0;

        int left = 0;
        int right = height.size() - 1;

        int max1 = height[left];
        int max2 = height[right];

        // Find maximum element on its left and right & Calculate maximum trapped water...
        while (left < right)
            if (max1 < max2) {
                trap += max1 - height[left];
                max1 = max(max1, height[++left]);
            } else {
                trap += max2 - height[right];
                max2 = max(max2, height[--right]);
            }
        return trap;    //return the amount..
    }
};
