// 4,2,0,3,2,5
       // first find the maxleft of each element 4 4 4 4 4 5
       // then find the maxright of each element 5 5 5 5 5 5 
       // then boundary =minimum of maxleft and maxright 4 4 4 4 4 5
       // then we substract boundary to height  and sum to get our answer 0+2+4+1+2+0=9

```
class Solution {
    public int trap(int[] height) {
        int left = 0, right = height.length - 1;
        int l_max = 0, r_max = 0;

        int res = 0;
        while (left < right) {
            l_max = Math.max(l_max, height[left]);
            r_max = Math.max(r_max, height[right]);

            if (l_max < r_max) {
                res += l_max - height[left];
                left++;
            } else {
                res += r_max - height[right];
                right--;
            }
        }
        return res;
    }
}

