class Solution {
    public int trap(int[] height) {
        int n = height.length;

        //first we have to create an arrray Left max to calculate height
        int LeftMax[] = new int[n];
        LeftMax[0] = height[0];
        for(int i=1; i<n; i++){
            LeftMax[i] = Math.max(height[i], LeftMax[i-1]);
        }
        // for calculating right max array 
        int RightMax[]=new int[n];
