class Solution {
    public int trap(int[] arr) {

        int res = 0;
        int n = arr.length;
        if(n == 1 || n == 2)return res;
        int left[] = new int[n] , right[] = new int[n];

        // finding left maximum hieght for every tower.
        left[0] = arr[0];
        for(int i = 1; i < n; i++){
            left[i] = Math.max(left[i-1],arr[i]);
        }
        // Finding the right tower height tower.
        right[n-1] = arr[n-1];
        for(int i = n-2; i >= 0; i--){
            right[i] = Math.max(arr[i],right[i+1]);
        }

        for(int i = 1; i < n-1; i++){
            res = res + (Math.min(left[i],right[i]) - arr[i]);
        }return res;
    }
}

