        //special case when len<6, insert
        else  if(len<6){
            steps+=6-len;
            New+=6-len;
        }
        //special case when one or some among lower case, upper case, and digit haven't shown up, even after the precedent modification
        if(3-lower-upper-digit>New) steps+=3-lower-upper-digit-New;
        return steps;
    }
};
