class Solution {
	public int strongPasswordChecker(String password) {
		int passLen = password.length();

		boolean noLowercase = true;
		boolean noUppercase = true;
		boolean noDigit = true;

		List<Integer> charRepeats = new ArrayList();
		char lastChar = 0;
		int lastRepeat = 0;

		for (int i = 0; i < passLen; i++) {
			char c = password.charAt(i);

			if (noUppercase && c > 64 && c < 91) {
				noUppercase = false;
			} else if (noLowercase && c > 96 && c < 123) {
				noLowercase = false;
			} else if (noDigit && c > 47 && c < 58) {
				noDigit = false;
			}

			if (lastChar == c) {
				lastRepeat++;
			}

			if (lastRepeat > 2 && (lastChar != c || i == passLen - 1)) {
				charRepeats.add(lastRepeat);
			}

			if (lastChar != c) {
				lastRepeat = 1;
				lastChar = c;
			}
		}

		int reusedCount = 0;

		if (noUppercase) {
			reusedCount++;
		}
		if (noLowercase) {
			reusedCount++;
		}
		if (noDigit) {
			reusedCount++;
		}

		reusedCount = Math.max(6 - passLen, reusedCount);
		int deleteCount = Math.max(0, passLen - 20);
		int addOrReplaceCount = 0;

		// when repeat times is multiple of 3, we can use one delete step to replace one other fix step
		for (int count : charRepeats) {
			if (count % 3 == 0 && deleteCount > 0) {
				deleteCount--;
				count--;
			}

			addOrReplaceCount += (int)Math.floor(count / 3.0f);
		}

		// when repeat times is multiple of 3 plus 1, we can use two delete steps to replace one other fix step
		for (int count : charRepeats) {
			if (count % 3 == 1 && deleteCount > 1) {
				deleteCount -= 2;
				addOrReplaceCount--;
			}
		}

		// if there still has delete steps, then we can still use three delete steps to replace one other fix step
		addOrReplaceCount -= (deleteCount / 3);

		// we should use the max between reusedCount and addOrReplaceCount, then plus delete items, because reused and deleted step are required
		return Math.max(reusedCount, addOrReplaceCount) + Math.max(0, passLen - 20);
	}
}

