int dp[50][22][2][2][2][65][65];    
class Solution {
public:
    // dp[0...i][j length][has_lower][has_upper][has_digit][last_char][last_to_last_char]
    bool isupper(char c) {
        return (c >= 'A' && c <= 'Z');
    }
    bool islower(char c) {
        return (c >= 'a' && c <= 'z');
    }
    bool isdigit(char c) {
        return (c >= '0' && c <= '9');
    }
    int getnum(char c) {
        if (c >= 'a' && c <= 'z') return c - 'a';
        if (c >= 'A' && c <= 'Z') return 26 + c - 'A';
        if (c >= '0' && c <= '9') return 52 + c - '0';
        return 64;
    }
    int dfs(const string& s, int pos, int taken, bool has_upper, bool has_lower, bool has_digit, int last_char, int last_to_last_char) {        
        if (taken > 20) return 1e9;

        if (pos == s.size()) {
            if (taken >= 6 && has_upper && has_lower && has_digit) return 0;
            if (taken < 6) {
              int res = 1 + dfs(s, pos, taken + 1, true, has_upper, has_digit, 63, 63);
              res = min(res, 1 + dfs(s, pos, taken + 1, has_lower, true, has_digit, 63, 63));
              return min(res, 1 + dfs(s, pos, taken + 1, has_lower, has_upper, true, 63, 63));
            }
            return 1e9;
        }
        int& res = dp[pos][taken][has_upper][has_lower][has_digit][last_char][last_to_last_char];
        if (res != -1) return res;
        res = 1e9;
        if (!(last_char == last_to_last_char && last_char == getnum(s[pos]))) {
            res = min(res, dfs(s, pos + 1, taken + 1, has_upper | isupper(s[pos]), has_lower | islower(s[pos]), has_digit | isdigit(s[pos]), getnum(s[pos]), last_char));
        }
        res = min(res, 1 + dfs(s, pos + 1, taken, has_upper, has_lower, has_digit, last_char, last_to_last_char));
        res = min(res, 1 + dfs(s, pos + 1, taken + 1, true, has_lower, has_digit, 63, last_char));
        res = min(res, 1 + dfs(s, pos + 1, taken + 1, has_upper, true, has_digit, 63, last_char));
        res = min(res, 1 + dfs(s, pos + 1, taken + 1, has_upper, has_lower, true, 63, last_char));
        res = min(res, 1 + dfs(s, pos, taken + 1, true, has_lower, has_digit, 63, last_char));
        res = min(res, 1 + dfs(s, pos, taken + 1, has_upper, true, has_digit, 63, last_char));
        res = min(res, 1 + dfs(s, pos, taken + 1, has_upper, has_lower, true, 63, last_char));
        return res;
    }
    int strongPasswordChecker(string s) {
        memset(dp, -1, sizeof(dp));
        return dfs(s, 0, 0, false, false, false, 63, 63);
    }
};
