class Solution {

    /**
     * @param String $password
     * @return Integer
     */
    function strongPasswordChecker($password) {
        $PasswordArray = str_split($password);//string to array

        $MissingType = 3;
        $NumList = range(48, 57);//(ASCII CODE)0 ~ 9
        $EngList_capital = range(65, 90);//(ASCII CODE)A ~ Z
        $EngList_lowercase = range(97, 122);//(ASCII CODE)a ~ z
        $NumFlag = $CapitalFlag = $LowercaseFlag = false;
        foreach($PasswordArray as $key=>$value){
            if(!$NumFlag && in_array(ord($value), $NumList)){ //check if is number
                $NumFlag = true;
            }else if(!$CapitalFlag && in_array(ord($value), $EngList_capital)){ //check if is A~Z
                $CapitalFlag = true;
            }else if(!$LowercaseFlag && in_array(ord($value), $EngList_lowercase)){ //check if is a~z
                $LowercaseFlag = true;
            }
        }
        if($NumFlag) $MissingType--;
        if($CapitalFlag) $MissingType--;
        if($LowercaseFlag) $MissingType--;

        /*
         * check repeat
         */
        $Change = $One = $Two = 0;
        $p = 2;
        while($p < strlen($password)){
            if($PasswordArray[$p]==$PasswordArray[$p-1] && $PasswordArray[$p]==$PasswordArray[$p-2]){
                $RepeatedLength = 2;
                while($p<strlen($password) && $PasswordArray[$p]==$PasswordArray[$p-1]){
                    $RepeatedLength ++;
                    $p ++;
                }
                $Change += floor($RepeatedLength/3);
                if($RepeatedLength%3==0) $One += 1;
                if($RepeatedLength%3==1) $Two += 2;
            }else{
                $p ++;
            }
        }

        if(strlen($password)<6){
            return max($MissingType, 6-strlen($password));
        }else if(strlen($password)>=6 && strlen($password)<=20){
            return max($MissingType, $Change);
        }else if(strlen($password)>20){
            $Delete = strlen($password) - 20;

            $Change -= min($Delete, $One);
            $Change -= floor(min(max($Delete - $One, 0), $Two) / 2);
            $Change -= floor(max($Delete - $One - $Two, 0) / 3);
            return $Delete + max($MissingType, $Change);
        }
    }
}

