    {

        public int StrongPasswordChecker(string s)
        {
            int digitsCount = 0;
            int lowerCaseCount = 0;
            int upperCaseCount = 0;


            foreach (var c in s)
            {
                if (c >= '0' && c <= '9')
                {
                    digitsCount++;
                    continue;
                }

                if (c >= 'a' && c <= 'z')
                {
                    lowerCaseCount++;
                    continue;
                }

                if (c >= 'A' && c <= 'Z')
                {
                    upperCaseCount++;
                }
            }

            int requireToHave = 0;

            if (digitsCount == 0)
            {
                requireToHave++;
            }

            if (lowerCaseCount == 0)
            {
                requireToHave++;
            }

            if (upperCaseCount == 0)
            {
                requireToHave++;
            }


            checked
            {
                int availableRemoves = Math.Max(0, s.Length - 6);
                int requiredRemoves = Math.Max(0, s.Length - 20);

                int availableAdditions = Math.Max(0, 20 - s.Length);
                int requiredAdditions = Math.Max(0, 6 - s.Length);

                bool[,,,] dp = new bool[s.Length + 1, availableRemoves + 1, availableAdditions + 1, s.Length + 1];

                for (int lengthIdx = 0; lengthIdx <= s.Length; lengthIdx++)
                {
                    for (int removeIdx = 0; removeIdx <= availableRemoves; removeIdx++)
                    {
                        for (int addIdx = 0; addIdx <= availableAdditions; addIdx++)
                        {
                            for (int replacementIdx = 0; replacementIdx <= s.Length; replacementIdx++)
                            {
                                dp[lengthIdx, removeIdx, addIdx, replacementIdx] = false;

                                if (lengthIdx == 0)
                                {
                                    if (removeIdx == 0 && replacementIdx == 0)
                                    {
                                        dp[lengthIdx, removeIdx, addIdx, replacementIdx] = true;
                                    }
                                    continue;
                                }


                                if (removeIdx + replacementIdx > lengthIdx)
                                {
                                    continue;
                                }


                                if (removeIdx != 0)
                                {
                                    dp[lengthIdx, removeIdx, addIdx, replacementIdx] |= dp[lengthIdx - 1, removeIdx - 1, addIdx, replacementIdx];
                                }

                                if (addIdx != 0)
                                {
                                    dp[lengthIdx, removeIdx, addIdx, replacementIdx] |= dp[lengthIdx - 1, removeIdx, addIdx - 1, replacementIdx];
                                }

                                if (replacementIdx != 0)
                                {
                                    dp[lengthIdx, removeIdx, addIdx, replacementIdx] |= dp[lengthIdx - 1, removeIdx, addIdx, replacementIdx - 1];
                                }


                                dp[lengthIdx, removeIdx, addIdx, replacementIdx] |= dp[lengthIdx - 1, removeIdx, addIdx, replacementIdx];

                                int lastCharIdx = lengthIdx - 1;
                                for (int i = lengthIdx - 1; i > 0; i--)
                                {
                                    bool needToBreak = false;
                                    if (s[i - 1] == s[lastCharIdx])
                                    {
                                        var repeatingLength = lastCharIdx - (i - 1) + 1;
                                        if (repeatingLength == 3)
                                        {
                                            needToBreak = true;
                                        }
                                    }
                                    else
                                    {
                                        lastCharIdx = i - 1;
                                    }

                                    dp[lengthIdx, removeIdx, addIdx, replacementIdx] |= dp[i, removeIdx, addIdx, replacementIdx];

                                    if (removeIdx != 0)
                                    {
                                        dp[lengthIdx, removeIdx, addIdx, replacementIdx] |= dp[i, removeIdx - 1, addIdx, replacementIdx];
                                    }

                                    if (addIdx != 0)
                                    {
                                        dp[lengthIdx, removeIdx, addIdx, replacementIdx] |= dp[i, removeIdx, addIdx - 1, replacementIdx];
                                    }

                                    if (replacementIdx != 0)
                                    {
                                        dp[lengthIdx, removeIdx, addIdx, replacementIdx] |= dp[i, removeIdx, addIdx, replacementIdx - 1];
                                    }

                                    if (needToBreak)
                                    {
                                        dp[lengthIdx, removeIdx, addIdx, replacementIdx] = false;

                                        for (int j = i; j < lengthIdx; j++)
                                        {
                                            if (addIdx != 0)
                                            {
                                                dp[lengthIdx, removeIdx, addIdx, replacementIdx] |= dp[j - 1, removeIdx, addIdx - 1, replacementIdx];
                                            }

                                            if (replacementIdx != 0)
                                            {
                                                dp[lengthIdx, removeIdx, addIdx, replacementIdx] |= dp[j - 1, removeIdx, addIdx, replacementIdx - 1];
                                            }
                                        }


                                        int idx = lastCharIdx;
                                        while (idx >= 0 && s[idx] == s[lastCharIdx])
                                        {
                                            idx--;
                                        }
                                        idx++;
                                        int repeatingCount = lastCharIdx - idx + 1;
                                        int removing = repeatingCount - 2;

                                        if (removeIdx - removing >= 0)
                                        {
                                            dp[lengthIdx, removeIdx, addIdx, replacementIdx] |= dp[idx, removeIdx - removing, addIdx, replacementIdx];

                                        }

                                        break;
                                    }
                                }
                            }
                        }
                    }
                }


                long res = int.MaxValue;

                for (int removeIdx = requiredRemoves; removeIdx <= availableRemoves; removeIdx++)
                {
                    for (int addIdx = requiredAdditions; addIdx <= availableAdditions; addIdx++)
                    {
                        for (int replacementIdx = 0; replacementIdx <= s.Length; replacementIdx++)
                        {
                            if (dp[s.Length, removeIdx, addIdx, replacementIdx] && addIdx + replacementIdx >= requireToHave)
                            {
                                res = Math.Min(res,  removeIdx + addIdx + replacementIdx);
                            }
                        }
                    }
                }

                return (int) res;
            }
        }
    }
