state description:
                 **i**: position in string.
                 **m**: number of successive identical chars seen so far. m ∈ {1,2,3}.
                 **cd**: number of conditions not met within lower,upper,digit. cd ∈ {0,1,2,3}.
                 **def**: num of chars missing.
                 **exc**: num of chars in excess.

```
class Solution {
public:
    int strongPasswordChecker(string s) {
        int n = s.length();
        int c[3] = {1,1,1};
        for(int i=0; i<n; ++i){
            if(c[0] && islower(s[i])){
                c[0] = 0;
            }
            else if (c[1] && isupper(s[i])){
                c[1] = 0;
            }
            else if (c[2] && isdigit(s[i])){
                c[2] = 0;
            }
        }       

        int count(0);    
        std::queue<std::array<int,5>> q;
        q.push({1,1,c[0] + c[1] + c[2],std::max(6-n,0),std::max(n-20,0)});

        while(!q.empty()){
            int sq = q.size(); 

            for(int j=0; j<sq; ++j){
                // state description:
                // i: position in string.
                // m: number of successive identical chars seen so far. m ∈ {1,2,3}.
                // cd: number of conditions not met within lower,upper,digit. cd ∈ {0,1,2,3}.
                // def: num of chars missing.
                // exc: num of chars in excess.
                auto [i,m,cd,def,exc] = q.front();
                q.pop();

                while(i<n){
                    if(s[i]==s[i-1]){
                        ++m;
                    }
                    else{
                        m=1;
                    }

                    if(m==3){ // action is needed here.

                        if(exc>0){
                            q.push({i+1,2,cd,def,exc-1}); // delete.
                            if(i==n-1 || s[i+1]!=s[i]){ // using a delete is optimal.
                               break;
                            }   
                        }
                        else if(def>0){
                            q.push({i+1,1,std::max(--cd,0),def-1,exc}); // insert.
                            if(i+2>=n || s[i+1] != s[i] || s[i+2] != s[i]){ // using an insert is optimal.
                                break;
                            }   
                        }
						// replace(no need to insert/delete or these actions are not guaranteed to be optimal).
                        q.push({i+1,0,std::max(cd-1,0),def,exc}); 
                        break;
                    } 
                    ++i;
                }

                if(i>=n){
                     if(def>0){
                         q.push({n,0,std::max(cd-1,0),def-1,exc});
                     }
                     else if (exc>0){
                         q.push({n,0,cd,def,exc-1});
                     }
                     else if(cd>0){
                         q.push({n,0,std::max(cd-1,0),def,exc});
                     }
                     else {
                         return count;   
                     }
                }

            } // for(int j=0; i<sz; ++j)
            count++;
        } // while (!q.empty())

        return -1;

    }
};

