class Solution:
    @staticmethod
    def classify(n):
        if n.islower():
            return 0
        if n.isdigit():
            return 2
        if n.isupper():
            return 1
        return 3

    def strongPasswordChecker(self, s):
        """
        :type s: str
        :rtype: int
        """
        l = len(s)
        if l < 4:
            return 6 - l
        state = [0, 0, 0, 0]
        repeat_cnt = 0
        repeat_arr = [[], [], []]
        tmp = 0
        t = Solution.classify(s[0])
        state[t] += 1
        for i in range(1, l + 1):
            if i < l:
                n = s[i]
                state[Solution.classify(n)] += 1
            else:
                n = ''
            if n == s[tmp]:
                continue
            else:
                length = i - tmp
                if length > 2:
                    repeat_cnt += length // 3
                    repeat_arr[length%3].append(length)
                tmp = i
        a = sum([0 if state[i] else 1 for i in range(3)])
        if repeat_cnt == 0:
            return max(a, 6 - l, l-20+a)
        else:
            if l < 6: 
                return max(a, repeat_cnt)
            if l > 20:  
                to_del_num = l - 20
                left_arr = []
                for i in range(3):
                    arr = repeat_arr[i]
                    for num in arr:
                        if to_del_num < i+1:
                            break
                        x = num - i - 1
                        to_del_num -= i+1
                        repeat_cnt -= 1
                        if x > 2:
                            left_arr.append(x)
                for num in left_arr:
                    if to_del_num < 3:
                        break
                    _ = min(num, to_del_num)// 3
                    to_del_num -= _ * 3
                    repeat_cnt -= _
                return l - 20 + max(a, repeat_cnt)
            return max(a, 6 - l, l-20+1, repeat_cnt)


