# Code
```
var strongPasswordChecker = function(p) {
  // Strategy:
  //
  // Identify runs (and their length) and count missing character deficiencies
  //
  // if < 6
  //   it just so happens that any run can be resolved by already needed additions and mutations
  //
  // if >= 6
  //   if > 20, count deletions while strategically reducing runs
  //   count mutation breaks needed to solve runs
  //   absorb character deficiencies into needed run breaks

  // Sort runs based on their divisibility by 3
  let runs = [[], [], []]
  let run

  let needL = 1, needU = 1, needD = 1

  for (let i = 0; i < p.length; i++) {
    if (needL && p[i] >= 'a' && p[i] <= 'z')
      needL = 0

    if (needU && p[i] >= 'A' && p[i] <= 'Z')
      needU = 0

    if (needD && p[i] >= '0' && p[i] <= '9')
      needD = 0

    if (p[i] === p[i-1])
      run++

    if (p[i] !== p[i-1] || i === p.length - 1) {
      if (run > 2)
        runs[run % 3].push(run)

      run = 1
    }
  }

  let needed = needL + needU + needD

  // Take care of the special case where all runs are autoamtically covered
  if (p.length < 6)
    return Math.max(6 - p.length, needed)

  let len = p.length, toDelete = Math.max(len - 20, 0), toChange = 0

  // Use password shortening to reduce runs
  while (len-- > 20) {
    // Focus on runs divisible by 3
    if (runs[0].length !== 0) {
      run = runs[0].pop() - 1

      if (run !== 2)
        runs[2].push(run)
    } else if (runs[1].length !== 0) {
      runs[0].push(runs[1].pop() - 1)
    } else if (runs[2].length !== 0) {
      runs[1].push(runs[2].pop() - 1)
    }
  }

  // Combine the runs into one list
  runs = runs.flat()

  toChange = runs.reduce(
    (total, run) => total + run / 3 | 0, 0
  )

  // Absorb character kind deficiencies into run breakup mutations
  needed = Math.max(needed - toChange, 0)

  return toDelete + toChange + needed
}
