int strongPasswordChecker(char * password){
    int length = strlen(password);
    int replace = 0;
    int one = 0;
    int two = 0;
    int cap = 1;
    int low = 1;
    int dig = 1;

    for(int i = 0; password[i]; i++){
        if(letters[password[i]] == 1){
            cap = 0;
        } else if(letters[password[i]] == 2) {
            low = 0;
        } else if(letters[password[i]] == 3) {
            dig = 0;
        }

        int repeat = 1;
        while(password[i] == password[i + 1]){
            i++;
            repeat += 1;
        }

        if(repeat > 2){
            replace += (repeat / 3);

            if(repeat % 3 == 0){
                one += 1;
            } else if(repeat % 3 == 1){
                two += 2;
            }
        }
    }

    if(length < 6){
        if(cap + low + dig > 6 - length){
            return cap + low + dig;
        } else {
            return 6 - length;
        }
    } else if(length <= 20){
        if(cap + low + dig > replace){
            return cap + low + dig;
        } else {
            return replace;
        }
    } else {
        int over = length - 20;

        if(one < over){
            replace -= one;
        } else {
            replace -= over;
        }

        if(over - one > 0){
            if(two < (over - one)){
                replace -= two / 2;
            } else {
                replace -= (over - one) / 2;
            }
        }

        if((over - one - two) > 0){
            replace -= (over - one - two) / 3;
        }

        if(cap + low + dig > replace){
            return over + cap + low + dig;
        } else {
            return over + replace;
        }
    }
}
