# Code
```
import re

UPPER = re.compile("[A-Z]")
LOWER = re.compile("[a-z]")
DIGIT = re.compile("[0-9]") 


def diversity_penalty(password):
    score = 0
    for r in [UPPER, LOWER, DIGIT]:
        if r.search(password):
            score += 1
    return 3 - score


def try_deleting(password, mod3):
    curr = 1
    to_check = "$" + password + "$"
    for i in range(1, len(to_check)):
        if to_check[i] == to_check[i - 1]:
            curr += 1
        else:
            if curr >= 3 and curr % 3 == mod3:
                return password[:i - 2] + password[i - 1:]
            curr = 1
    return None



def count_replacements(password):
    curr = 1
    to_check = "$" + password + "$"
    score = 0
    for i in range(1, len(to_check)):
        if to_check[i] == to_check[i - 1]:
            curr += 1
        else:
            score += curr // 3
            curr = 1
    return score


class Solution:

    def strongPasswordChecker(self, password: str) -> int:

        div_penalty = diversity_penalty(password)

        if len(password) < 6:
            return max(6 - len(password), div_penalty)

        original_password = password

        while len(password) > 20:
            new_password = try_deleting(password, 0) or try_deleting(password, 1) or try_deleting(password, 2)
            if not new_password:
                return len(original_password) - 20 + div_penalty
            password = new_password

        n_replacements = count_replacements(password)
        return max(len(original_password) - 20, 0) + max(n_replacements, div_penalty) 


