```c++
class Solution {
public:
    int strongPasswordChecker(string& s) {
        int len[50 / 3], cnt = 0;                    // lengths of each group of more than 2 same characters
        bool digit = false, lower = false, upper = false;
        for (unsigned i = 0, j; i < s.size(); i = j) {
            digit = digit || isdigit(s[i]);
            lower = lower || islower(s[i]);
            upper = upper || isupper(s[i]);
            for (j = i + 1; j < s.size() && s[j] == s[i]; ++j);
            if (j - i >= 3) len[cnt++] = j - i;
        }
        int con = (!digit) + (!lower) + (!upper);    // need more condictions to be satisfied
        int ins = s.size() < 6  ? 6 - s.size()  : 0; // need more insertions
        int del = s.size() > 20 ? s.size() - 20 : 0; // need more deletions
        int res = 0;                                 // operations have been done
        for (int i = 0; i < cnt; ++i) {
            while (len[i] >= 3 && ins > 0) { // Insert a character between same characters when more insertion is needed.
                ++res;
                --ins;
                len[i] -= 2;                 // Reduces the length of same characters by 2, eg. "AAAA..." -> "AAaAA...".
                if (con) --con;              // We can always find a character that satisfies more condiction.
            }
            while (len[i] >= 3 && con > 0) { // Replace a character in row of same characters to satisfy more condiction.
                ++res;
                --con;
                len[i] -= 3;                 // Reduces the length of same characters by 3, eg. "AAAA..." -> "AAaA...".
            }
        }
        // Then if it need deletions, we should first delete characters from the same N (N>=3)
        // characters in a row which N % 3 == 0, then N % 3 == 1 and N % 3 == 2.
        // Because K replacements are needed to separate k*3 same characters but K-1 replacements are need for K*3-1 same characters.
        // For example, 2 characters must be deleted from "AAAAAA789012345678aAAA" since its length is 22.
        // If two 'A' characters are deleted from front, 2 more replacements are needed.
        // But if one 'A' is deleted from front and the other from back, only 1 replacement is needed.
        for (int mod = 0; mod < 3; ++mod) {
            for (int i = 0; i < cnt; ++i) {
                while (len[i] >= 3 && del > mod && len[i] % 3 == mod) {
                    res += mod + 1;
                    del -= mod + 1;
                    len[i] -= mod + 1;
                }
            }
        }
        con -= min(con, ins);                            // More insertions can satisfy one more condictions.
        for (int i = 0; i < cnt; ++i) res += len[i] / 3; // Perform additional replacements to separete same characters.
        return res + ins + con + del;
    }
};
