class Solution {
    public boolean isLowercase(char c){
        return c>='a' && c<='z';
    }
    public boolean isUppercase(char c){
        return c>='A' && c<='Z';
    }
    public boolean isDigit(char c){
        return c>='0' && c<='9';
    }
    public int[] getInfo(String s){
	// to generate the table
        int arr[] = {0,0,0};
        int count = 1;
        int index = 1;
        while(index<s.length()){
            if(s.charAt(index) == s.charAt(index-1))
                ++count;
            else{
                if(count>=3){
                    if(count%3==0){
                        ++arr[0];
                        arr[2] += (count-1)/3;
                    }
                    else if(count%3==1){
                        ++arr[1];
                        arr[2] += (count-2)/3;
                    }
                    else
                        arr[2] += count/3;
                }
                count=1;
            }

            ++index;
        }
        if(count>=3){
           if(count%3==0){
                ++arr[0];
                arr[2] += (count-1)/3;
            }
            else if(count%3==1){
                ++arr[1];
                arr[2] += (count-2)/3;
            }
            else
                arr[2] += count/3; 
        }

        return arr;
    }
    public int strongPasswordChecker(String password) {
        if(password.length()<4)
            return 6 - password.length();
        else if(password.length() <= 5){
            boolean lc = false;
            boolean uc = false;
            boolean dt = false;
            int count = 0;
            for(int i=0;i<password.length();++i){
                if(isLowercase(password.charAt(i)))
                    lc = true;
                else if(isUppercase(password.charAt(i)))
                    uc = true;
                else if(isDigit(password.charAt(i)))
                    dt = true;
            }
            if(!lc)
                ++count;
            if(!uc)
                ++count;
            if(!dt)
                ++count;
            if(count < 6-password.length())
                count = 6-password.length();
            return count;
        }
        else{
            boolean lc = false;
            boolean uc = false;
            boolean dt = false;
            int repeat = 1;
            int needToReplace = 0;
            if(isLowercase(password.charAt(0)))
                lc = true;
            else if(isUppercase(password.charAt(0)))
                uc = true;
            else if(isDigit(password.charAt(0)))
                dt = true;
            for(int i=1;i<password.length();++i){
                if(isLowercase(password.charAt(i)))
                    lc = true;
                else if(isUppercase(password.charAt(i)))
                    uc = true;
                else if(isDigit(password.charAt(i)))
                    dt = true;
                if(password.charAt(i) == password.charAt(i-1))
                    ++repeat;
                else
                    repeat = 1;
                if(repeat==3){
                    ++needToReplace;
                    repeat = 0;
                }
            }
            int count = 0;
            if(!lc)
                ++count;
            if(!uc)
                ++count;
            if(!dt)
                ++count;
            if(password.length()<=20)
                return Math.max(needToReplace,count);
            int needToErase = password.length()-20;
            int needToErase_copy = needToErase;
            int common = 0;
            int[] arr = getInfo(password);
            // calculate the "common"
            int current_index = 0;
            while(current_index<3 && needToErase_copy>0){
                int temp = Math.min(needToErase_copy/(current_index+1),arr[current_index]);
                common += temp;
                needToErase_copy -= temp * (current_index+1);
                ++current_index;
            }
            return Math.max(needToReplace-common,count) + needToErase;
        }
    }
}
