```
function strongPasswordChecker(password: string): number
{
    let adds: number = Math.max(0, 6 - password.length);
    let subs: number = Math.max(0, password.length - 20);
    let lower: number = (password.match(/[a-z]/g) ?? []).length == 0 ? 1 : 0;
    let upper: number = (password.match(/[A-Z]/g) ?? []).length == 0 ? 1 : 0;
    let digit: number = (password.match(/[0-9]/g) ?? []).length == 0 ? 1 : 0;
    let mods: number = lower + upper + digit;
    let tris: number = (password.match(/(.)\1{2,2}/g) ?? []).length;

    let grps = (password.match(/(.)\1{2,}/g) ?? []).map((x) => { return x.length; });
    for (let i = subs; i && grps.length; i--) {
        grps = grps.filter((x) => { return x >= 3; }).sort((a, b) => { return (a % 3) - (b % 3); });
        tris -= (grps[0] % 3 == 0) ? 1 : 0;
        grps[0]--;
    }

    let steps: number = 0;
    steps = Math.max(steps, tris);
    steps = Math.max(steps, mods);
    steps = Math.max(steps, adds);
    steps = steps + subs;
    return steps;
};
