            if(password[i]>='0'&&password[i]<='9') digit=1;
            else if(password[i]>='a'&&password[i]<='z') lower=1;
            else if(password[i]>='A'&&password[i]<='Z') upper=1;
            //when illegal string such as s="aaa" appears, try to modify it 
            if(repeat>=3&&(i==password.size()-1||(i<password.size()-1&&password[i]!=password[i+1]))){
                //if len>6, insert.
                   if(len<6){
                        steps+=repeat/2-1;
                        New+=repeat/2-1;
                        len+=repeat/2-1;
                    }
                //else, replace
                    else{
                        steps+=repeat/3;
                        New+=repeat/3;
                        cnt[repeat%3]++;
                    }
                }
            //update the length of repeating chars
            if(i<password.size()-1&&password[i]==password[i+1]) repeat++;
            else repeat=1;
        }
        //if len-20>0
        if(remain>0) {
            //delete
            steps+=remain;
            //greedy. the illegal strings with smaller i==len(s)%3 have higher priority to be deleted. When k chars are deleted, k/(i+1) replacements are cancelled, resulting in decrease of steps
            for(int i=0;i<3;i++){
                    if(remain<cnt[i]*(i+1)){
                        steps-=remain/(i+1);
                        New-=remain/(i+1);
                        remain=0;
                        break;
                    }
                    steps-=cnt[i];
                    New-=cnt[i];
                    remain-=cnt[i]*(i+1);
                }
            //Now all illegal strings s have len(s)%3==2. When k chars are deleted, k/3 replacements are cancelled, resulting in decrease of steps
            if(remain<New*3){
                steps-=remain/3;
                New-=remain/3;
            }
            else {
                steps-=New;
                New=0;
        }            
