class Solution {
    func strongPasswordChecker(_ password: String) -> Int {
        let pw = Array(password), pwCount = pw.count
        var low = false, up = false, num = false
        for pwCount in pw {
            if pwCount.isLowercase { low = true }
            else if pwCount.isUppercase { up = true }
            else if pwCount.isNumber { num = true }
        }
        var types = 3
        if low { types -= 1 }
        if up { types -= 1 }
        if num { types -= 1 }

        var one = 0, two = 0, rep = 0, i = 2
        while i < pwCount {
            if pw[i] == pw[i-1] && pw[i] == pw[i-2] {
                var length = 2
                while i < pwCount && pw[i] == pw[i-1] {
                    length += 1
                    i += 1
                }
                rep += length/3
                if length % 3 == 0 { one += 1 }
                else if length % 3 == 1 { two += 1 }
            } else {
                i += 1
            }
        }
        if pwCount < 6 {
            return max(types, 6 - pwCount)
        } else if pwCount <= 20 {
            return max(types, rep)
        }
        let del = pwCount - 20
        rep -= min(del, one)
        rep -= (min(max(del - one, 0), two * 2) / 2)
        rep -= (max(del - one - 2 * two, 0) / 3)
        return max(types, rep) + del
    }
}
