                cnt++;
            } else {
                if(cnt >= 3) {
                    repeat.push_back(cnt);
                }
                c = s[i];
                cnt = 1;
            }
        }
        if(cnt >= 3) {
            repeat.push_back(cnt);
        }
        for(auto x: repeat) {
            if(x % 3 == 0) { 
                rep_1 += 1; 
            }
            if(x % 3 == 1) {
                rep_2 += 1;
            }
            rep += x / 3;
        }
    }

