class Solution {
public:

    int repeat(string &A){

        vector<string>div;
        int start = 0, i = 0;


        for(i = 1; i<A.size(); i++){

            if(A[i] != A[i-1]){
                div.push_back(A.substr(start,i-start));
                start = i;
            }            

        }

        if(A[i] != A[i-1]){
            div.push_back(A.substr(start,i-start));
        }   

        for(int i = 0; i < div.size(); i++){

            if(div[i].size() >= 3){
                break;
            }
            else if(i == div.size()-1){
                return -1;
            }

        }

        int index = 0;
        int x = -1, y = -1;

        for(int i = 0; i<div.size(); i++){

            int m = div[i].size();

            if(m < 3){
                index += m;
                continue;
            }

            if(m % 3 == 0){

                return index;

            }

            if(m % 3 == 1 and x == -1){

                x = index;

            }

            if(m % 3 == 2 and y == -1){

                y = index;

            }

            index += m;

        }

        if(x != -1){
            return x;
        }
        if(y != -1){
            return y;
        }        


        return 0;

    }


    int check(string &A){

        int total = 3;
        bool f1 = true, f2 = true, f3 = true;

        for(int i = 0; i < A.size(); i++){

            char x = A[i];

            if(x >= 65 and x <= 90 and f1){
                total--;
                f1 = false;
            } 
            if(x >= 97 and x <= 122 and f2){
                total--;
                f2 = false;
            }
            if(x >= 48 and x <= 57 and f3){
                total--;
                f3 = false;
            }

        } 

        return total;   

    }

    int check(string &A, int j){

        int ans = 0;

        for(int i = j-2; i>=0; i--){

            if(A[i] == A[i+1] and A[i] == A[i+2]){
                ans++;
                i -= 2;
            }

        }

        return ans;

    }



    int strongPasswordChecker(string password) {

        int want = check(password);
        int ans = 0;

        if(password.size() < 6){
            int c = check(password,password.size()-1);
            int d = 6-password.size();
            ans = max(c,want);
            ans = max(ans,d);
            return ans;
        }

        while(password.size() > 20){
            int ind = repeat(password);
            if(ind == -1){
                break;
            }
            password.erase(password.begin() + ind);     
            ans++;
        }

        int rep = 0;

        while(true){
            int ind = repeat(password);
            if(ind == -1){
                break;
            }
            password[ind + 2] = '#';    
            rep++;
        }

