                    if(groups[i]>=3 && groups[i]%3==1){
                        // can delete one of two depending on n
                        cur_delete = min(2, n-20) ;
                        deletions += cur_delete;
                        groups[i] -= cur_delete ;

                        n-=cur_delete;

                        cur_delete_cnt++;
                    }
                }

                for(int i=0; i<m && n>20; i++){
                    // pick group with cnt%3==2
                    if(groups[i]>=3 && groups[i]%3==2){
                        // can delete one of two or three depending on n
                        cur_delete = min(3, n-20) ;
                        deletions += cur_delete;
                        groups[i] -= cur_delete ;
                        n-=cur_delete;

                        cur_delete_cnt++;
                    }
                }

                if(cur_delete_cnt == 0){
                    break;
                }

            }

            // now either there are no groups of size >=3 and n>20 still
            // or there are some, but n==20
            int req_repl = cnt_min_repl(groups);

            //if(dm) trace3(req_repl, n, deletions);

            assert(req_repl == 0 || n==20) ;

            // Mistake : forgot this
            if(n>20){
                assert(req_repl==0);
                deletions += n-20;
                n=20;
            }

            ans = deletions + max(req_repl, req_insert) ;

        }


        return ans;

    }
};
