class Solution {
public:
int lastdel,lasttwodel,lastthreedel=0;
int findRepitiveCount(string str, int n)
{
    int rep=1;
    int i=0;
    int j=i+1;
    int count=0;

    while(i<n&&j<=n)
    {
        if(str[i]==str[j])
        {
            rep++;
            j++;
            if(rep%3==0)
            {
                count++;
            }
        }
        else
        {
            if(rep%3==0)
                lastdel++;
            else if(rep%3==1&&rep>3)
                lasttwodel++;
            else if(rep%3==2&&rep>3)
                lastthreedel++;

            rep=1;
            i=j++;
        }
    }
    return count;
}


