    {
        r=n-20;
    }
    int rcount=findRepitiveCount(str, n);
    bool isDigit=false;
    bool isUpper=false;
    bool isSmall=false;
    int count=0;
    for(int i=0;i<n;i++)
    {
        if(str[i]>=65&&str[i]<=90)
        {
            isUpper=true;
        }
        else if(str[i]>=97&&str[i]<=122)
        {
            isSmall=true;
        }
        else if(str[i]-'0'>=0&&str[i]-'0'<=9)
        {
            isDigit=true;
        }
    }

    if(!isUpper)
        count++;
    if(!isDigit)
        count++;
    if(!isSmall)
        count++;

    if(n<6)
    {
        int i=6-n;
        return max(count,i);
    }

    rcount=rcount-min(r,lastdel);
    rcount=rcount-min(max(r-lastdel, 0), 2*lasttwodel)/2;
    rcount=rcount-min(max(r-lastdel-2*lasttwodel, 0),3*lastthreedel)/3;
    rcount=rcount-max(r-lastdel-2*lasttwodel-3*lastthreedel, 0) /3;
    count=r+max(rcount,count);
    return count;
    }
};

