#include <iostream>

// #define DEBUG

#ifdef DEBUG
#define P(X)\
cout << #X": " << (X) << endl;
#else
#define P(X)
#endif

int AddViolation(const string& pw) {
    if(pw.size() < 6) { return 6-pw.size(); }
    return 0;
}

int RemoveViolation(const string& pw) {
    if(pw.size() > 20) { return pw.size() - 20; }
    return 0;
}


int LCViolation(const string& pw) {
    for(auto c : pw) {
        if(c == '?' || (c >= 'a' && c <= 'z')) {
            return 0;
        }
    }
    return 1;
}

int UCViolation(const string& pw) {
    for(auto c : pw) {
        if(c == '?' || (c >= 'A' && c <= 'Z')) {
            return 0;
        }
    }
    return 1;
}

int DigViolation(const string& pw) {
    for(auto c : pw) {
        if(c == '?' || (c >= '0' && c <= '9')) {
            return 0;
        }
    }
    return 1;
}

vector<int> Sequences(const string& pw) {
    // Find all sequences of equal chars.
    vector<int> result;
    int ii = 1;
    int cur_rep = 1;
    while(ii < pw.size()) {
        if(pw[ii] == pw[ii-1]) {
            ++cur_rep;
        } else {
            // Just broke the current repetition.
            if(cur_rep >= 3) {
                result.push_back(cur_rep);
            }
            cur_rep = 1;
        }
        ++ii;
    }
    if(cur_rep >= 3) {
        result.push_back(cur_rep);
    }
    return result;
}

int SequenceViolation(const string& pw, int change, int add, int remove) {
    auto sequences = Sequences(pw);

    auto remove_short_sequences = [&sequences] {
        sequences.erase(
            std::remove_if(sequences.begin(), sequences.end(), [](int s){ return s<3; }),
            sequences.end()
        );    
    };
    auto print_sequences = [&sequences] {
        for(int s : sequences) {
            cout << s << ", ";
        }
        cout << endl;
        return "";
    };

    remove_short_sequences();
    if(sequences.empty()) { return 0; }

    P(change)
    P(add)
    P(remove)

    for(auto sinit : sequences) {
        P(sinit)
    }

    // change
    P("change")
    while(change > 0 && !sequences.empty()) {
        // sort(sequences.begin(), sequences.end(), [](auto a, auto b) { return (a/2)%3 > (b/2)%3;});
        sort(sequences.begin(), sequences.end(), [](auto a, auto b) { return a%3 < b%3;});
        P(print_sequences())
        auto& s = sequences.front();
        int sorig = s-1;
        // int s1 = floor(sorig/2);
        int s1 = 2;
        int s2 = sorig - s1;
        s = s1;
        sequences.push_back(s2);
        --change;
        remove_short_sequences();
    }

    P("after change")
    P(print_sequences())

    // add
    P("add")
    while(add > 0 && !sequences.empty()) {
        // sort(sequences.begin(), sequences.end(), [](auto a, auto b) { return (a/2)%3 > (b/2)%3;});
        sort(sequences.begin(), sequences.end(), [](auto a, auto b) { return a%3 < b%3;});
        P(print_sequences())
        auto& s = sequences.front();
        int sorig = s;
        // int s1 = floor(sorig/2);
        int s1 = 2;
        int s2 = sorig - s1;
        s = s1;
        sequences.push_back(s2);
        --add;
        remove_short_sequences();
    }

    P("after add")
    P(print_sequences())

    // remove
    P("remove")
    while(remove > 0 && !sequences.empty()) {
        sort(sequences.begin(), sequences.end(), [](auto a, auto b) { return a%3 < b%3;});
        P(print_sequences())
        auto& s = sequences.front();
        --s;
        --remove;
        remove_short_sequences();
    }

    P("after remove")
    P(print_sequences())


    int violation = 0;
    for(auto s : sequences) {
        violation += s/3;
    }

    return violation;
}


class Solution {
public:
    int strongPasswordChecker(string password) {
        P(password)

        int remove = RemoveViolation(password);
        int add = AddViolation(password);
        int lc = LCViolation(password);
        int uc = UCViolation(password);
        int dig = DigViolation(password);

        P(remove)
        P(add)
        P(lc)
        P(uc)
        P(dig)

        int changes = max(0, dig+lc+uc-add);
        int seq = SequenceViolation(password, changes, add, remove);

        P(changes)
        P(seq)
        P(remove)
        P(add)

        return remove+add+changes+seq;
    }
};
```

Not the prettiest code, but works. It's based on counting the number of changes required to fix the trivial violations (lower case, upper case, digits, addition and removal) and figuring out how many of those you can use to fix the tricky sequence constraint violations.

