```

class Solution {
public:
    // 1. [6, 20] characters
    // 2. one lower case, one uppercase, one digit
    // 3. no 3 repeating characters
    int strongPasswordChecker(string password) {

        int cnt[3] = {0};

        if (password.size() <= 3) 
            return 6 - password.size(); // inserstions only

        for (int i = 0; i < password.size(); i++) {
            if (password[i] >= 'a' && password[i] <= 'z')
                cnt[0] = 1;
            else if (password[i] >= 'A' && password[i] <= 'Z')
                cnt[1] = 1;
            else if (password[i] >= '0' && password[i] <= '9')
                cnt[2] = 1;
        }

        int missing = 3 - (cnt[0] + cnt[1] + cnt[2]);

        if (password.size() == 4) {
            if (missing == 3)
                return 3;  // one replacement and two insertions
            return 2;      // two insertions
        }
        else if (password.size() == 5) {
            if (missing == 3)
                return 3; // two replacement, and one insertion
            else if (missing == 2)
                return 2; // one replacement, and one insertion
            else if (missing == 1)
                return 1; // one insertion;
            else
                return 1; // one insertion
        }
        else if (password.size() >= 6 && password.size() <= 20) { 
            int i = 1;
            int replacements = 0;
            while(i < password.size()) {
                if (password[i] == password[i - 1]) {
                    int j = i - 1;
                    while(i < password.size() && password[i] == password[i - 1]) i++;
                    replacements += (i - j) / 3;
                }
                else {
                    i++;
                }
            }

            // when replacements > missing,  we can replace to isolate repeatings
            // when replacements <= missing, we need "missing" num of replacements to cover missings
            return max(replacements, missing);
        }
        else {
            // password.size() > 20
            int extra_cnt = password.size() - 20;
            int i = 1;
            int replacements = 0;
            int deleted = 0;
            vector<int> size_seq;
            while(i < password.size()) {
                if (password[i] == password[i - 1]) {
                    int j = i - 1;
                    while(i < password.size() && password[i] == password[i - 1]) i++;
                    int size = i - j;
                    if ((size % 3) == 0 && extra_cnt) { 
                        // cover case of 3, 6, 9 ... 
                        // if it is 3, only need delete one charater to avoid repeating
                        size --;
                        deleted ++;
                        extra_cnt --;
                    }
                    if (size >= 3) {
                        size_seq.push_back(size);
                    }

                }
                else {
                    i++;
                }
            }

            for (int i = 0; i < size_seq.size() && extra_cnt >= 2; i++) {
                if (size_seq[i] > 3 && (size_seq[i] % 3) == 1) {
                    // cover case of 4, 7, 10 ...
                    // if it is 4, only need delete 2 charaters to avoid repeating
                    deleted += 2;
                    extra_cnt -= 2;
                    size_seq[i] -= 2;
                }
            }

            for (int i = 0; i < size_seq.size(); i++) {
                while (size_seq[i] >= 3 && extra_cnt >= 3) {
                    // for remaining cases, delete 3 characters each time
                    deleted += 3;
                    extra_cnt -= 3;
                    size_seq[i] -= 3;
                }
                replacements += size_seq[i] / 3;
            }

            // deleted , remaining extra cnt, max of replacements and missing
            return deleted + extra_cnt + max(replacements, missing);
        }
    }
};
