            }//if

            int numToReplace = 0;
            for (i = troublesomeLength; i < tooLongFrequency.length; i++)
            {
                numToReplace += (tooLongFrequency[i] * (i / troublesomeLength));
            }//for

            numEdits = numRemoved + Math.max(numToReplace, numDifferentTypesNeeded);

