        public class Solution
        {
            public int StrongPasswordChecker(string s)
            {
                bool lower = false, upper = false, digit = false;
                Dictionary<int, int> lengthToCount = new Dictionary<int, int>();
                foreach (var charA in s)
                {
                    lower = Char.IsLower(charA) | lower;
                    upper = Char.IsUpper(charA) | upper;
                    digit = Char.IsDigit(charA) | digit;

                    if (lower & upper & digit)
                    {
                        break;
                    }
                }

                int replacesToBeMade = (lower ? 0 : 1) + (upper ? 0 : 1) + (digit ? 0 : 1);
                bool isLength = false;
                int length = 0;
                for (int i = 1; i < s.Length; i++)
                {
                    if (s[i] == s[i - 1])
                    {
                        if (!isLength)
                        {
                            isLength = true;
                            length = 2;
                        }
                        else
                        {
                            length++;
                        }
                    }
                    else
                    {
                        if (isLength)
                        {
                            isLength = false;
                            if (length >= 3)
                            {
                                if (!lengthToCount.ContainsKey(length))
                                {
                                    lengthToCount[length] = 0;
                                }
                                lengthToCount[length]++;
                            }
                            length = 0;
                        }
                    }
