                if (toAdd > 0)
                {
                    if (lengthToCount.ContainsKey(5))
                    {
                        return 2;
                    }

                    return toAdd;
                }
                var keysSorted = lengthToCount.Keys.ToArray();
                Array.Sort(keysSorted);

                int totalToHandle = lengthToCount.Values.Sum();
                int handled = 0;
                foreach (var lengthSeq in keysSorted)
                {
                    for (int i = 0; i < lengthToCount[lengthSeq]; i++)
                    {
                        handled++;
                        if (totalToHandle == handled || toDelete <= 0 || keysSorted.Length == 1)
                        {
                            int toFixInReplace = lengthSeq / 3;
                            if (totalToHandle == handled)
                            {
                                toFixInReplace = (lengthSeq - toDelete) / 3;
                                toDelete = 0;
                            }
                            if (toFixInReplace <= replacesToBeMade || toDelete <= 0)
                            {
                                replacesToBeMade -= toFixInReplace;
                            }
                            else
                            {
                                toFixInReplace -= replacesToBeMade;
                                toDelete -= (lengthSeq - 3 * replacesToBeMade) - 3 + 1;
                                replacesToBeMade = 0;
                            }
                        }
                        else
                        {
                            toDelete -= lengthSeq - 3 + 1;
                        }
                    }
                }
                var done = toReplaceGen + Math.Max(0, (-1) * replacesToBeMade) + Math.Max(0, (-1) * toDelete) + toDeleteGen;
